import { useRecoilState, useRecoilValue } from "recoil";
import { accountAtom } from "../atom/accountAtom";
import Logo from "../assets/logo.png";
import { navbarData, staffNavbarData } from "../data/navbarData";
import { Form, Link, useNavigate } from "react-router-dom";
import { useEffect, useState } from "react";

const Navbar = () => {
  const [account, setAccount] = useRecoilState(accountAtom);
  const [currentURL, setCurrentURL] = useState(window.location.href);
  const navigate = useNavigate();

  const unhidden = () => {
    const profileMenu = document.querySelector("div.hidden");
    profileMenu.classList.remove("hidden");
  };

  const hidden = () => {
    const profileMenu = document.getElementById("profileMenu");
    if (!profileMenu.classList.contains("hidden")) {
      profileMenu.classList.add("hidden");
    }
  };

  const submitSearch = (event) => {
    event.preventDefault();
    const search = new FormData(event.target).get("search");
    navigate(`/search/${search}`)
  }

  return (
    <>
      <div className="flex justify-between items-center px-10 py-3 border border-neutral-700">
        <div className="flex items-center">
          <img style={{ width: "100px" }} src={Logo} className="mr-2" />
          <Form onSubmit={submitSearch} method="post">
            <input
              name="search"
              placeholder="Tìm kiếm trên BlogAnimal"
              className="p-3 rounded-full bg-neutral-700 text-neutral-300"
              required
            />
          </Form>
        </div>

        <div className="flex items-center text-neutral-500 w-1/3">
          {navbarData.map((data) => (
            <Link onClick={() => setCurrentURL(data.alt)} to={data.link}>
              {data.alt == currentURL ? (
                <data.icon
                  sx={{ fontSize: "50px" }}
                  className="text-white mr-5"
                />
              ) : (
                <data.icon sx={{ fontSize: "50px" }} className="mr-5" />
              )}
            </Link>
          ))}
          {account?.roleId && account?.roleId == 1 ? (
            <>
              {staffNavbarData.map((data) => (
                <Link onClick={() => setCurrentURL(data.alt)} to={data.link}>
                  {data.alt == currentURL ? (
                    <data.icon
                      sx={{ fontSize: "50px" }}
                      className="text-white mr-5"
                    />
                  ) : (
                    <data.icon sx={{ fontSize: "50px" }} className="mr-5" />
                  )}
                </Link>
              ))}
            </>
          ) : (
            ""
          )}
        </div>
        <div className="flex items-center">
          {account !== undefined ? (
            <>
              <div onMouseEnter={unhidden} className="flex items-center">
                <img
                  className="w-10 rounded-full mr-3"
                  src="data:image/png;base64,"
                />
                <div className="text-white text-lg font-medium">
                  {account?.username}
                </div>
              </div>
              <div
                onMouseLeave={hidden}
                id="profileMenu"
                className="hidden absolute mt-56 bg-neutral-700 rounded-2xl text-white font-medium z-10"
              >
                <div className="flex items-center px-5 pt-3">
                  <img
                    className="w-7 rounded-full mr-3"
                    src="data:image/png;base64,"
                  />
                  <div>{account?.username}</div>
                </div>
                <div className="bg-neutral-500 h-0.5 my-3 w-full"></div>
                <div
                  onClick={() => {
                    setAccount(undefined);
                    navigate("/");
                  }}
                  className="text-center pb-3"
                >
                  Logout
                </div>
                <div className="bg-neutral-500 h-0.5 my-3 w-full"></div>
                <Link
                  to={`Profile/${account?.accountId}`}
                  className="block text-center pb-3"
                >
                  Profile
                </Link>
              </div>
            </>
          ) : (
            <div className="flex">
              <button
                onClick={() => navigate("SignIn")}
                className="bg-blue-800 text-white font-medium p-2 rounded-2xl mr-3"
              >
                Đăng Kí
              </button>
              <button
                onClick={() => navigate("SignIn")}
                className="bg-blue-800 text-white font-medium p-2 rounded-2xl"
              >
                Đăng Nhập
              </button>
            </div>
          )}
        </div>
      </div>
    </>
  );
};

export default Navbar;
