import { useEffect, useState } from "react";
import { Link, useLoaderData } from "react-router-dom";
import ThumbUpOffAltIcon from "@mui/icons-material/ThumbUpOffAlt";
import ChatBubbleOutlineIcon from "@mui/icons-material/ChatBubbleOutline";
import SendIcon from "@mui/icons-material/Send";
import api from "../../api/api";
import { useRecoilValue } from "recoil";
import { accountAtom, backgroundState } from "../../atom/accountAtom";
import Modal from "@mui/material/Modal";
import Box from "@mui/material/Box";
import {
  createPostImagemodalStyle,
  createPostmodalStyle,
} from "../../style/style";
import { PhotoArrageImgUrl } from "../../components/PhotoArrage";
import { CommentModal } from "../../components/Modals/CommentModal";
import { PostImagesModal } from "../../components/PostImagesModal";
import { useNavigate } from "react-router-dom";
import MenuIcon from "@mui/icons-material/Menu";
import DeleteIcon from "@mui/icons-material/Delete";

export const SearchPage = () => {
  const bg = useRecoilValue(backgroundState)
  const account = useRecoilValue(accountAtom);
  const { posts, blogs } = useLoaderData();
  const [show, setShow] = useState("Post");

  const [postForCommentModal, setPFCM] = useState();
  const [postForImageModal, setPFIM] = useState();
  const [openCM, setOpenCM] = useState(false);
  const [openPI, setOpenPI] = useState(false);
  const navigate = useNavigate();
  const handleOpenCM = (post) => {
    if (account) {
      setPFCM(post);
      setOpenCM(true);
    } else {
      navigate("/SignIn");
    }
  };
  const handleOpenPI = () => setOpenPI(true);

  const handleClose = async () => {
    setOpenCM(false);
    setOpenPI(false);
    const getPosts = await api.getAllPost();
    setPosts(getPosts);
  };

  const likePost = async (postId) => {
    const result = await api.likePost(postId, account.accountId);
    console.log(result);
    const getPosts = await api.getAllPost();
    setPosts(getPosts.filter((post) => post.isSecure));
  };

  return (
    <>
      <div className="flex">
        <div className="w-1/3 mt-10">
          <div onClick={() => setShow("Post")}
            className={`${bg=="dark"?"bg-neutral-700":"bg-white border"} rounded-xl w-2/3 p-2 text-right mb-3 font-medium ${
                show == "Post" ? (bg=="dark"?"text-white":"text-black")  : "text-neutral-400"
            }`}
          >
            Bài Post
          </div>
          <div onClick={() => setShow("Blog")} className={`${bg=="dark"?"bg-neutral-700":"bg-white border"} rounded-xl w-2/3 p-2 text-right font-medium ${
                show == "Blog" ? (bg=="dark"?"text-white":"text-black") : "text-neutral-400"
            }`}>
            Bài Blog
          </div>
        </div>
        <div className="w-2/3">
          {show == "Post" ? (
            <>
              <div className="text-white mt-10 pb-5">
                {posts?.map((post) => (
                  <>
                    <div
                      key={post.postId}
                      className={`rounded-xl w-11/12 m-auto ${bg=="dark"?"bg-neutral-800":"bg-white border-2 text-black"} mb-20`}
                    >
                      <div className="pt-3 mx-5">
                        <div className="font-medium text-lg flex items-center mb-3 justify-between">
                          <div className="flex">
                            <img
                              className="w-7 rounded-full mr-1"
                              src="data:image/png;base64,"
                            />
                            {post?.account?.username}
                          </div>
                          {(account &&
                            post?.account?.accountId === account?.accountId) ||
                          account?.roleId === 1 ? (
                            <div
                            className={`p-3 ${bg=="dark"?"bg-neutral-800":"bg-white"} menudiv`}
                              onClick={(event) => {
                                const deleteBtn = event.target
                                  .closest(".menudiv")
                                  .querySelector("div");
                                if (!deleteBtn.classList.contains("hidden")) {
                                  deleteBtn.classList.add("hidden");
                                } else {
                                  deleteBtn.classList.remove("hidden");
                                }
                              }}
                            >
                              <MenuIcon />
                              <div
                                onClick={async () => {
                                  await api.deletePost(post.postId);
                                  await CallBack();
                                }}
                                className="absolute hidden bg-neutral-800 py-3 px-5"
                              >
                                <div className="text-red-500 flex items-center">
                                  <DeleteIcon /> Xóa
                                </div>
                              </div>
                            </div>
                          ) : (
                            ""
                          )}
                        </div>
                        <div className="font-light text-neutral-400">
                          {post?.createdDate}
                        </div>
                      </div>
                      <div className="my-5 mx-5 pl-2 font-medium">
                        {post?.title}
                      </div>
                      <div
                        className="my-5 mx-5 pl-2"
                        dangerouslySetInnerHTML={{ __html: post?.content }}
                      ></div>

                      {post?.images ? (
                        <div
                          onClick={() => {
                            setPFIM(post);
                            handleOpenPI();
                          }}
                          className="my-5 mx-5 pl-2"
                        >
                          <PhotoArrageImgUrl urls={post?.images?.split(",")} />
                        </div>
                      ) : (
                        ""
                      )}

                      <hr />
                      <div className="flex justify-between text-neutral-400 mx-10 py-5">
                        <div
                          className={`${
                            post.postLikes.some(
                              (like) => like.accountId == account?.accountId
                            )
                              ? "text-blue-500"
                              : ""
                          }`}
                          onClick={() => {
                            if (account) {
                              likePost(post.postId);
                            } else {
                              navigate("/SignIn");
                            }
                          }}
                        >
                          <ThumbUpOffAltIcon /> Thích{" "}
                          {post.postLikes.length > 0
                            ? post.postLikes.length
                            : ""}
                        </div>
                        <div onClick={() => handleOpenCM(post)}>
                          <ChatBubbleOutlineIcon />
                          Bình luận
                        </div>
                        <div>
                          <SendIcon /> Chia sẻ
                        </div>
                      </div>
                      <hr />
                      {post?.postComments[0] ? (
                        <>
                          <div>
                            <a
                              onClick={() => handleOpenCM(post)}
                              className="ml-3 text-neutral-400"
                            >
                              Xem thêm bình luận
                            </a>
                            <div className="flex m-5 pb-3">
                              <div>
                                <img
                                  className="w-7 rounded-full mr-3"
                                  src="data:image/png;base64,"
                                />
                              </div>
                              <div className={`pl-3 ${bg==="dark"?"bg-neutral-700":"bg-white border"} p-3 rounded-2xl`}>
                                <div className="font-semibold mb-2">
                                  {post?.postComments[0]?.account.username}
                                </div>
                                <div>{post?.postComments[0]?.content}</div>
                              </div>
                            </div>
                          </div>
                        </>
                      ) : (
                        ""
                      )}

                      <div className="flex m-5 pb-3 items-center">
                        <div>
                          <img
                            className="w-7 rounded-full mr-3"
                            src="data:image/png;base64,"
                          />
                        </div>
                        <input
                          onClick={() => handleOpenCM(post)}
                          className={`pl-3 p-3 w-full ${bg==="dark"?"bg-neutral-700 text-neutral-400":"bg-white border text-black"} rounded-2xl`}
                          placeholder="comment..."
                        />
                      </div>
                    </div>
                  </>
                ))}
              </div>

              {/* modal */}

              <Modal
                open={openCM}
                onClose={handleClose}
                aria-labelledby="modal-modal-title"
                aria-describedby="modal-modal-description"
              >
                <Box sx={createPostmodalStyle}>
                  <CommentModal
                    handleClose={handleClose}
                    Post={postForCommentModal}
                  />
                </Box>
              </Modal>

              <Modal
                open={openPI}
                onClose={handleClose}
                aria-labelledby="modal-modal-title"
                aria-describedby="modal-modal-description"
              >
                <Box sx={createPostImagemodalStyle}>
                  <PostImagesModal Post={postForImageModal} />
                </Box>
              </Modal>
            </>
          ) : (
            <>
              <div className="mt-10 px-20 flex flex-wrap">
                {blogs.map((blog) => (
                  <Link
                    to={`/Blog/${blog.blogId}`}
                    className="w-full h-60 rounded-2xl bg-neutral-700 mr-10 px-5 py-5 text-white"
                  >
                    <div className="text-2xl my-5  font-serif font-medium">
                      {blog.title}
                    </div>
                    <div
                      className=" mr-5 h-1/3 overflow-hidden"
                      dangerouslySetInnerHTML={{ __html: blog?.content }}
                    ></div>
                    <span>...</span>
                  </Link>
                ))}
              </div>
            </>
          )}
        </div>
      </div>
    </>
  );
};
