import { useEffect, useState } from "react";
import ThumbUpOffAltIcon from "@mui/icons-material/ThumbUpOffAlt";
import ChatBubbleOutlineIcon from "@mui/icons-material/ChatBubbleOutline";
import SendIcon from "@mui/icons-material/Send";
import api from "../../api/api";
import { useRecoilValue } from "recoil";
import { accountAtom } from "../../atom/accountAtom";
import Modal from "@mui/material/Modal";
import Box from "@mui/material/Box";
import {
  createPostImagemodalStyle,
  createPostmodalStyle,
} from "../../style/style";
import {
  CreatePostModal,
  CreateTradeModal,
} from "../../components/Modals/Modal";
import { PhotoArrageImgUrl } from "../../components/PhotoArrage";
import {
  CommentModal,
  TradeCommentModal,
} from "../../components/Modals/CommentModal";
import { PostImagesModal } from "../../components/PostImagesModal";
import { Link, useNavigate } from "react-router-dom";
import MenuIcon from "@mui/icons-material/Menu";
import DeleteIcon from "@mui/icons-material/Delete";
import ArtTrackIcon from "@mui/icons-material/ArtTrack";
import PaidIcon from "@mui/icons-material/Paid";
import CurrencyExchangeIcon from '@mui/icons-material/CurrencyExchange';
import PostAddIcon from '@mui/icons-material/PostAdd';

const TradePost = () => {
  //const posts = useLoaderData();
  const account = useRecoilValue(accountAtom);
  const [postForCommentModal, setPFCM] = useState();
  const [postForImageModal, setPFIM] = useState();
  const [posts, setPosts] = useState([]);
  const [usPosts, setUSPosts] = useState([]);
  const [open, setOpen] = useState(false);
  const [openCM, setOpenCM] = useState(false);
  const [openPI, setOpenPI] = useState(false);
  const navigate = useNavigate();
  const handleOpen = () => setOpen(true);
  const handleOpenCM = (post) => {
    if (account) {
      setPFCM(post);
      setOpenCM(true);
    } else {
      navigate("/SignIn");
    }
  };
  const handleOpenPI = () => setOpenPI(true);

  const CallBack = async () => {
    const getPosts = await api.getAllTradePost();
    setPosts(getPosts.filter(post => post.isSecure));
    setUSPosts(getPosts.filter(post => !post.isSecure))
  };

  useEffect(() => {
    CallBack();
  }, []);

  const handleClose = async () => {
    setOpen(false);
    setOpenCM(false);
    setOpenPI(false);
    await CallBack()
    
  };

 
  return (
    <>
      <div className="text-white mt-20 pb-32">
        {account?.roleId === 1 ? (
          <div className=" w-5/12 m-auto mb-10">
            <Link
              to={`/UnsecureTrade`}
              className="bg-neutral-800 inline-block p-3 rounded-xl"
            >
              <PostAddIcon />
              <div className="absolute rounded-full bg-red-600 px-2 ml-3 mb-3">
                {usPosts.length}
              </div>
            </Link>
          </div>
        ) : (
          ""
        )}
        {account ? (
          <div className="rounded-xl w-5/12 m-auto bg-neutral-800 p-5 mb-10">
            <div className="flex mb-3 items-center">
              <img
                className="w-8 rounded-full mr-1"
                src="data:image/png;base64,"
              />
              <div className="text-xl font-medium">{account?.username}</div>
            </div>
            <input
              onClick={handleOpen}
              className="rounded-full p-2 w-full bg-neutral-600"
              placeholder={account?.username + " what are you thinking ?"}
            />
          </div>
        ) : (
          ""
        )}
        {posts?.map((post) => (
          <>
            <div
              key={post.tradeId}
              className="rounded-xl w-5/12 m-auto bg-neutral-800 mb-20"
            >
              <div className="pt-3 mx-5">
                <div className="font-medium text-lg flex items-center mb-3 justify-between">
                  <Link to={`/Profile/${post?.account?.accountId}`} className="flex">
                    <img
                      className="w-7 rounded-full mr-1"
                      src="data:image/png;base64,"
                    />
                    {post?.account?.username}
                  </Link>
                  {(account &&
                    post?.account?.accountId === account?.accountId) ||
                  account?.roleId === 1 ? (
                    <div
                      className="p-3 bg-neutral-800 menudiv"
                      onClick={(event) => {
                        const deleteBtn = event.target
                          .closest(".menudiv")
                          .querySelector("div");
                        if (!deleteBtn.classList.contains("hidden")) {
                          deleteBtn.classList.add("hidden");
                        } else {
                          deleteBtn.classList.remove("hidden");
                        }
                      }}
                    >
                      <MenuIcon />
                      <div className="absolute hidden bg-neutral-800 py-3 px-5">
                        <div
                          onClick={async () => {
                            await api.deleteTrade(post.tradeId);
                            await CallBack();
                          }}
                          className="text-red-500 flex items-center"
                        >
                          <DeleteIcon /> Delete
                        </div>
                        <div
                          onClick={async () => {
                            await api.isTrade(post.tradeId);
                            await CallBack();
                          }}
                          className="text-blue-500 flex items-center mt-3"
                        >
                          <CurrencyExchangeIcon /> Transacted
                        </div>
                      </div>
                    </div>
                  ) : (
                    <div className="p-3 bg-neutral-800 menudiv">
                      <MenuIcon />
                    </div>
                  )}
                </div>
                <div className="font-light text-neutral-400">
                  {post?.createdDate}
                </div>
              </div>
              {/* <div className="my-5 mx-5 pl-2 font-medium">{post?.title}</div> */}
              <div
                className="my-5 mx-5 pl-2"
                dangerouslySetInnerHTML={{ __html: post?.content }}
              ></div>

              {/* {post?.images ? (
                <div
                  onClick={() => {
                    setPFIM(post);
                    handleOpenPI();
                  }}
                  className="my-5 mx-5 pl-2"
                >
                  <PhotoArrageImgUrl urls={post?.images?.split(",")} />
                </div>
              ) : (
                ""
              )} */}

              <div className="bg-neutral-700 h-1 w-full" />
              <div className="flex justify-between text-neutral-400 mx-10 py-5">
                {/* <div
                  className={`${
                    post.postLikes.some(
                      (like) => like.accountId == account?.accountId
                    )
                      ? "text-blue-500"
                      : ""
                  }`}
                  onClick={() => {
                    if (account) {
                      likePost(post.postId);
                    } else {
                      navigate("/SignIn");
                    }
                  }}
                >
                  <ThumbUpOffAltIcon /> Thích{" "}
                  {post.postLikes.length > 0 ? post.postLikes.length : ""}
                </div> */}
                <div onClick={() => handleOpenCM(post)}>
                  <ChatBubbleOutlineIcon />
                  Bình luận
                </div>
                {post.isTrade ? (
                  <div className="text-green-500">
                    <PaidIcon /> Đã giao dịch
                  </div>
                ) : (
                  <div className="text-yellow-500">
                    <PaidIcon /> Đang giao dịch
                  </div>
                )}
              </div>
              <div className="bg-neutral-700 h-1 w-full mb-3" />
              {post?.tradeComments[0] ? (
                <>
                  <div>
                    <a
                      onClick={() => handleOpenCM(post)}
                      className="ml-3 text-neutral-400"
                    >
                      Xem thêm bình luận
                    </a>
                    <div className="flex m-5 pb-3">
                      <div>
                        <img
                          className="w-7 rounded-full mr-3"
                          src="data:image/png;base64,"
                        />
                      </div>
                      <div className="pl-3 bg-neutral-700 p-3 rounded-2xl">
                        <div className="font-semibold mb-2">
                          {post?.tradeComments[0]?.account.username}
                        </div>
                        <div>{post?.tradeComments[0]?.content}</div>
                      </div>
                    </div>
                  </div>
                </>
              ) : (
                ""
              )}

              <div className="flex m-5 pb-3 items-center">
                <div>
                  <img
                    className="w-7 rounded-full mr-3"
                    src="data:image/png;base64,"
                  />
                </div>
                <input
                  onClick={() => handleOpenCM(post)}
                  className="pl-3 p-3 w-full text-neutral-400 bg-neutral-700 rounded-2xl"
                  placeholder="comment..."
                />
              </div>
            </div>
          </>
        ))}
      </div>

      {/* modal */}
      <Modal
        open={open}
        onClose={handleClose}
        aria-labelledby="modal-modal-title"
        aria-describedby="modal-modal-description"
      >
        <Box sx={createPostmodalStyle}>
          <CreateTradeModal handleClose={handleClose} />
        </Box>
      </Modal>

      <Modal
        open={openCM}
        onClose={handleClose}
        aria-labelledby="modal-modal-title"
        aria-describedby="modal-modal-description"
      >
        <Box sx={createPostmodalStyle}>
          <TradeCommentModal
            handleClose={handleClose}
            Post={postForCommentModal}
          />
        </Box>
      </Modal>

      <Modal
        open={openPI}
        onClose={handleClose}
        aria-labelledby="modal-modal-title"
        aria-describedby="modal-modal-description"
      >
        <Box sx={createPostImagemodalStyle}>
          <PostImagesModal Post={postForImageModal} />
        </Box>
      </Modal>
    </>
  );
};

export default TradePost;
